import { useEffect, useState } from "react";
import Container from "react-bootstrap/Container";
import Row from "react-bootstrap/Row";
import Col from "react-bootstrap/Col";
import Table from "react-bootstrap/Table";
import Button from "react-bootstrap/Button";
import Form from "react-bootstrap/Form"; // Import Form component for search bar
import { useNavigate } from "react-router-dom";

const Dashboard = () => {
  const [employees, setEmployees] = useState([]); // Initialize to empty array
  const [filteredEmployees, setFilteredEmployees] = useState([]); // To store the filtered list
  const [page, setPage] = useState(0);
  const [totalPages, setTotalPages] = useState(0);
  const [searchQuery, setSearchQuery] = useState(""); // New state for search input
  const navigate = useNavigate();

  useEffect(() => {
    fetchEmployees(page, 10); // Fetch initial 10 records when page changes
  }, [page]);

  // Update filtered employees when employees or searchQuery changes
  useEffect(() => {
    const filtered = employees.filter((employee) =>
      employee.name.toLowerCase().includes(searchQuery.toLowerCase())
    );
    setFilteredEmployees(filtered);
  }, [searchQuery, employees]);

  const fetchEmployees = async (page, size) => {
    try {
      const response = await fetch(`http://localhost:8080/api/page/employees?page=${page}&size=${size}`);
      const data = await response.json();

      if (data && data.content) {
        setEmployees(data.content); // Set employee data
        setFilteredEmployees(data.content); // Set filtered data initially to the same
        setTotalPages(data.totalPages); // Set total pages
      } else {
        setEmployees([]); // Default to empty array if no data is returned
        setFilteredEmployees([]);
      }
    } catch (error) {
      console.error("Error fetching employees:", error.message);
      setEmployees([]); // Set to empty array if error occurs
      setFilteredEmployees([]);
    }
  };

  const handleDelete = async (employeeId) => {
    try {
      const response = await fetch(`http://localhost:8080/api/employees/${employeeId}`, {
        method: "DELETE",
      });
      if (response.ok) {
        fetchEmployees(page, 10); // Refresh employees
        console.log(`Employee with ID ${employeeId} deleted successfully`);
      }
    } catch (error) {
      console.log("Error deleting employee:", error.message);
    }
  };

  const handleUpdate = (employeeId) => {
    navigate(`/employee/${employeeId}`);
  };

  const nextPage = () => {
    if (page < totalPages - 1) {
      setPage((prev) => prev + 1); // Move to the next page
    }
  };

  const prevPage = () => {
    if (page > 0) {
      setPage((prev) => prev - 1); // Move to the previous page
    }
  };

  return (
    <Container className="mt-5">
      <Row>
        <Col>
          <h1 className="text-center">Employees</h1>

          {/* Search bar */}
          <Form.Control
            type="text"
            placeholder="Search by name"
            value={searchQuery}
            onChange={(e) => setSearchQuery(e.target.value)} // Update searchQuery state on change
            className="mb-3"
          />

          <Table striped bordered hover responsive>
            <thead>
              <tr>
                <th>Name</th>
                <th>Email</th>
                <th>Phone</th>
                <th>Department</th>
                <th>Action</th>
              </tr>
            </thead>
            <tbody>
              {filteredEmployees.length > 0 ? (
                filteredEmployees.map((employee) => (
                  <tr key={employee.id}>
                    <td>{employee.name}</td>
                    <td>{employee.email}</td>
                    <td>{employee.phone}</td>
                    <td>{employee.department}</td>
                    <td>
                      <Button variant="outline-warning" onClick={() => handleUpdate(employee.id)}>
                        Update
                      </Button>{" "}
                      <Button variant="outline-danger" onClick={() => handleDelete(employee.id)}>
                        Delete
                      </Button>
                    </td>
                  </tr>
                ))
              ) : (
                <tr>
                  <td colSpan="5" className="text-center">
                    No employees found
                  </td>
                </tr>
              )}
            </tbody>
          </Table>

          {/* Pagination Controls */}
          <div className="d-flex justify-content-between mt-3">
            <Button onClick={prevPage} disabled={page === 0}>
              Previous
            </Button>
            <Button onClick={nextPage} disabled={page >= totalPages - 1}>
              Next
            </Button>
          </div>
        </Col>
      </Row>
    </Container>
  );
};

export default Dashboard;
