import { useEffect, useState } from "react";
import Container from "react-bootstrap/Container";
import Row from "react-bootstrap/Row";
import Col from "react-bootstrap/Col";
import Table from "react-bootstrap/Table";
import Button from "react-bootstrap/Button";
import Form from "react-bootstrap/Form";
import { useNavigate } from "react-router-dom";

const Dashboard = () => {
  const [employees, setEmployees] = useState([]);
  const [page, setPage] = useState(0);
  const [totalPages, setTotalPages] = useState(0);
  const navigate = useNavigate();

  useEffect(() => {
    fetchEmployees(page, 10); // Fetch initial 10 records
  }, [page]);

  const fetchEmployees = async (page, size) => {
    try {
      const response = await fetch(`http://localhost:8080/api/employees?page=${page}&size=${size}`);
      const data = await response.json();
      setEmployees(data.content); // Set employee data
      setTotalPages(data.totalPages); // Set total pages
    } catch (error) {
      console.error("Error fetching employees:", error.message);
    }
  };

  const handleDelete = async (employeeId) => {
    try {
      const response = await fetch(`http://localhost:8080/api/employee/${employeeId}`, {
        method: "DELETE",
      });
      if (response.ok) {
        fetchEmployees(page, 10); // Refresh employees
        console.log(`Employee with ID ${employeeId} deleted successfully`);
      }
    } catch (error) {
      console.log("Error deleting employee:", error.message);
    }
  };

  const handleUpdate = (employeeId) => {
    navigate(`/employee/${employeeId}`);
  };

  const nextPage = () => {
    if (page < totalPages - 1) {
      setPage((prev) => prev + 1); // Move to the next page
    }
  };

  const prevPage = () => {
    if (page > 0) {
      setPage((prev) => prev - 1); // Move to the previous page
    }
  };

  return (
    <Container className="mt-5">
      <Row>
        <Col>
          <h1 className="text-center">Employees</h1>

          <Table striped bordered hover responsive>
            <thead>
              <tr>
                <th>Name</th>
                <th>Email</th>
                <th>Phone</th>
                <th>Department</th>
                <th>Action</th>
              </tr>
            </thead>
            <tbody>
              {employees.map((employee) => (
                <tr key={employee.id}>
                  <td>{employee.name}</td>
                  <td>{employee.email}</td>
                  <td>{employee.phone}</td>
                  <td>{employee.department}</td>
                  <td>
                    <Button variant="outline-warning" onClick={() => handleUpdate(employee.id)}>
                      Update
                    </Button>{" "}
                    <Button variant="outline-danger" onClick={() => handleDelete(employee.id)}>
                      Delete
                    </Button>
                  </td>
                </tr>
              ))}
            </tbody>
          </Table>

          {/* Pagination Controls */}
          <div className="d-flex justify-content-between mt-3">
            <Button onClick={prevPage} disabled={page === 0}>
              Previous
            </Button>
            <Button onClick={nextPage} disabled={page >= totalPages - 1}>
              Next
            </Button>
          </div>
        </Col>
      </Row>
    </Container>
  );
};

export default Dashboard;
