import { useEffect, useState } from "react";
import Container from "react-bootstrap/Container";
import Row from "react-bootstrap/Row";
import Col from "react-bootstrap/Col";
import Table from "react-bootstrap/Table";
import Button from "react-bootstrap/Button";
import Form from "react-bootstrap/Form";
import Pagination from "react-bootstrap/Pagination"; // Import Bootstrap Pagination
import { useNavigate } from "react-router-dom";

const Dashboard = () => {
    const [employees, setEmployees] = useState([]); // Initialize employee list
    const [searchQuery, setSearchQuery] = useState(""); // For search input
    const [isSearching, setIsSearching] = useState(false); // For showing if a search is in progress
    const [currentPage, setCurrentPage] = useState(0); // Current page state
    const [totalPages, setTotalPages] = useState(1); // Total pages state
    const [pageSize] = useState(5); // Page size (e.g., 5 items per page)
    const navigate = useNavigate();

    // Fetch employees by name and with pagination
    useEffect(() => {
        fetchEmployeesByName(searchQuery, currentPage, pageSize);
    }, [searchQuery, currentPage, pageSize]);

    const fetchEmployeesByName = async (name, page, size) => {
      setIsSearching(true);
      try {
          // Fetch employees by name, or all if name is empty
          const response = await fetch(
              `http://localhost:8080/api/employees/search?name=${name || ''}&page=${page}&size=${size}`
          );
  
          if (!response.ok) {
              throw new Error("Network response was not ok");
          }
  
          const data = await response.json();
          console.log("API Response:", data);
  
          if (data && data.content && Array.isArray(data.content)) {
              setEmployees(data.content); // Set employees
              setTotalPages(data.totalPages); // Set total pages
          } else {
              setEmployees([]);
          }
      } catch (error) {
          console.error("Error fetching employees:", error.message);
          alert("Error fetching employee data. Please try again.");
          setEmployees([]);
      } finally {
          setIsSearching(false);
      }
  };
  

    const handleDelete = async (employeeId) => {
        if (window.confirm("Are you sure you want to delete this employee?")) {
            try {
                const response = await fetch(`http://localhost:8080/api/employees/${employeeId}`, {
                    method: "DELETE",
                });
                if (response.ok) {
                    fetchEmployeesByName(searchQuery, currentPage, pageSize); // Refresh employee list after deletion
                } else {
                    alert("Failed to delete employee. Please try again.");
                }
            } catch (error) {
                console.error("Error deleting employee:", error.message);
                alert("Error deleting employee. Please try again.");
            }
        }
    };

    const handleUpdate = (employeeId) => {
        navigate(`/employee/${employeeId}`);
    };

    // Handle page change for pagination
    const handlePageChange = (newPage) => {
        setCurrentPage(newPage);
    };

    return (
        <Container className="mt-5">
            <Row>
                <Col>
                    <h1 className="text-center">Employee Details</h1>

                    {/* Search bar */}
                    <Form.Control
                        type="text"
                        placeholder="Enter employee name"
                        value={searchQuery}
                        onChange={(e) => setSearchQuery(e.target.value)}
                        className="mb-3"
                    />

                    {/* Loading Spinner */}
                    {isSearching && <p>Loading...</p>}

                    {/* Employee table */}
                    <Table striped bordered hover responsive>
                        <thead>
                            <tr>
                                <th>ID</th>
                                <th>Name</th>
                                <th>Email</th>
                                <th>Phone</th>
                                <th>Department</th>
                                <th>Action</th>
                            </tr>
                        </thead>
                        <tbody>
                            {employees.length > 0 ? (
                                employees.map((employee) => (
                                    <tr key={employee.id}>
                                        <td>{employee.id}</td>
                                        <td>{employee.name}</td>
                                        <td>{employee.email}</td>
                                        <td>{employee.phone}</td>
                                        <td>{employee.department}</td>
                                        <td>
                                            <Button
                                                variant="outline-warning"
                                                onClick={() => handleUpdate(employee.id)}
                                            >
                                                Update
                                            </Button>{" "}
                                            <Button
                                                variant="outline-danger"
                                                onClick={() => handleDelete(employee.id)}
                                            >
                                                Delete
                                            </Button>
                                        </td>
                                    </tr>
                                ))
                            ) : (
                                <tr>
                                    <td colSpan="6" className="text-center">
                                        No employees found
                                    </td>
                                </tr>
                            )}
                        </tbody>
                    </Table>

                    
                </Col>
            </Row>
        </Container>
    );
};

export default Dashboard;
